const A=(d=1500,t="BTC")=>{const s=[],i={BTC:{basePrice:45e3+Math.random()*1e4,volatility:.02,trend:.001},ETH:{basePrice:2500+Math.random()*1e3,volatility:.025,trend:.0012},BNB:{basePrice:300+Math.random()*100,volatility:.03,trend:8e-4},ADA:{basePrice:.5+Math.random()*.3,volatility:.035,trend:.0015}},a=i[t]||i.BTC;let e=a.basePrice;for(let o=0;o<d;o++){const l=Math.sin(o/100)*a.trend,h=(Math.random()-.5)*a.volatility,M=l+h,c=e*(1+M),n=e,r=c,m=Math.max(n,r)*(1+Math.random()*.01),p=Math.min(n,r)*(1-Math.random()*.01);s.push({open:parseFloat(n.toFixed(t==="ADA"?4:2)),high:parseFloat(m.toFixed(t==="ADA"?4:2)),low:parseFloat(p.toFixed(t==="ADA"?4:2)),close:parseFloat(r.toFixed(t==="ADA"?4:2)),volume:Math.floor(Math.random()*1e6)+1e5,timestamp:null}),e=c}return s};export{A as generateMockData};
